#
# Export variables to control UNIFYCR runtime settings.
#

#
# Source a script that is dynamically generated by 0001-setup.t.
#
. $UNIFYCR_TEST_RUN_SCRIPT

UNIFYCR_MOUNT_POINT=${UNIFYCR_MOUNT_POINT:-$(mktemp -d)}
UNIFYCR_META_DB_PATH=${UNIFYCR_META_DB_PATH:-$(mktemp -d)}
UNIFYCR_USE_SPILLOVER=${UNIFYCR_USE_SPILLOVER:-1}
UNIFYCR_META_SERVER_RATIO=${UNIFYCR_META_SERVER_RATIO:-1}
UNIFYCR_META_DB_NAME=${UNIFYCR_META_DB_NAME:-unifycr_db}
UNIFYCR_CHUNK_MEM=${UNIFYCR_CHUNK_MEM:-0}
DEBUG_FILE="$UNIFYCR_META_DB_PATH/unifycrd_debuglog"
UNIFYCR_SERVER_DEBUG_LOG=${UNIFYCR_SERVER_DEBUG_LOG:-$DEBUG_FILE}
UNIFYCR_EXTERNAL_DATA_DIR=${UNIFYCR_EXTERNAL_DATA_DIR:-$UNIFYCR_META_DB_PATH}
UNIFYCR_EXTERNAL_META_DIR=${UNIFYCR_EXTERNAL_META_DIR:-$UNIFYCR_META_DB_PATH}

export UNIFYCR_MOUNT_POINT
export UNIFYCR_META_DB_PATH
export UNIFYCR_USE_SPILLOVER
export UNIFYCR_META_SERVER_RATIO
export UNIFYCR_META_DB_NAME
export UNIFYCR_SERVER_DEBUG_LOG
export UNIFYCR_EXTERNAL_DATA_DIR
export UNIFYCR_EXTERNAL_META_DIR
